import React, { useState, useEffect } from 'react';
import { Button, FormControl, InputLabel, Input, FormHelperText } from '@material-ui/core';
import './App.css';
import Message from './Message';
import db from './firebase';
import firebase from 'firebase';
import FlipMove from 'react-flip-move';


function App() {

  const [input, setInput] = useState('');
  const [messages, setMesssages] = useState([]);
  const [username, setUsername] = useState('');

  useEffect(() => {
    setUsername(prompt('Please enter you name'))
  }, [])

  useEffect(() => {

    db.collection('messages')
    .orderBy('timestamp', 'asc')
    .onSnapshot(snapshot => {
      setMesssages(snapshot.docs.map(doc => ({id: doc.id ,message: doc.data()})))
    })
  }, [])

  const sendMessage = (event) => {
    event.preventDefault();

    db.collection('messages').add({
      message: input,
      username: username,
      timestamp: firebase.firestore.FieldValue.serverTimestamp()
    })

    setInput('');
  }

  return (
    <div className="App">
      <h1>Facebook messenger </h1>
      <h2>Welcome {username}</h2>

      <FlipMove>
      {
      messages.map(({id, message}) => (
        <Message key={id} username={username} message={message} />

      ))

    }
    </FlipMove>

      <form>
          <FormControl>
            <InputLabel >Enter a message...</InputLabel>
            <Input value={input} onChange={event => setInput(event.target.value)}/>
            <Button variant="contained" disabled={!input} color="primary" type='submit' onClick={sendMessage}>Send message </Button>

          </FormControl>


      </form>


    </div>



  );
}

export default App;
